var open=document.querySelector(".modal-open"),popup=document.querySelector(".modal"),close=popup.querySelector(".modal-close"),$name=popup.querySelector("#name-field"),email=popup.querySelector("#email-field"),form=popup.querySelector(".message");open.addEventListener("click",function(evt){evt.preventDefault(),popup.classList.add("modal-show")}),close.addEventListener("click",function(evt){evt.preventDefault(),popup.classList.remove("modal-show"),popup.classList.remove("modal-error")}),form.addEventListener("submit",function(evt){$name.value&&email.value||(evt.preventDefault(),popup.classList.remove("modal-error"),popup.offsetWidth=popup.offsetWidth,popup.classList.add("modal-error"),console.log("Нужно ввести имя и адрес почты"))}),window.addEventListener("keydown",function(evt){27===evt.keyCode&&(evt.preventDefault(),popup.classList.contains("modal-show")&&(popup.classList.remove("modal-show"),popup.classList.remove("modal-error")))});